<div id="user_info_modal" class="modal">
    <div class="modal-wrapper">
        <div class="user-info-modal-input__wrapper">
            <div class="modal-title">Изменение пользователя</div>
            <label class="user-info-label">Имя</label>
            <input type="text" class="modal-input" name="name" autocomplete="off">
        </div>
        <div class="user-info-modal-input__wrapper">
            <label class="user-info-label">Логин</label>
            <input type="text" class="modal-input" name="login" autocomplete="off">
        </div>
        <div id="save_changes_btn" class="modal-btn">Сохранить изменения</div>
        <div class="modal-user-dlt"><span id="delete_user_btn">Удалить пользователя</span></div>
    </div>
</div>